import React, {useRef, useState} from "react";
import "./styles/header.css";
import {FiShoppingCart} from "react-icons/fi";
import {useNavigate} from "react-router-dom";
import {Link} from "react-router-dom";
import {CgProfile} from "react-icons/cg";
import {useDispatch, useSelector} from "react-redux";

export default function Header() {
  const [value, changeValue] = useState("Регистрация");
  const dispatch = useDispatch();
  const profileColor = useSelector((state) => state.profileColor);
  const basketColor = useSelector((state) => state.basketColor);
  const userEmail = useSelector((state) => state.userEmail);
  const userPassword = useSelector((state) => state.userPassword);
  function changeProfileColor() {
    dispatch({type: "CHANGE_PROFILE_COLOR", color: "#3cba0c"});
  }
  const email = useRef();
  const password = useRef();
  const reg = useRef();
  function registration() {
    if (userEmail && userPassword) {
      email.current.value = userEmail;
      password.current.value = userPassword;
    }
    reg.current.classList.toggle("registrationWidth");
  }
  return (
    <div className="head">
      <div className="header">
        <img src="/images/logo.png" alt="Figrolls" className="header__logo" />
        <div className="header__main">
          <Link to="/main">Главная</Link>
          <p>О нас</p>
          <p>Контакты</p>
        </div>
        <div className="header__cart">
          <CgProfile
            onClick={() => registration()}
            cursor="pointer"
            color={profileColor}
            size={20}
          />
          <FiShoppingCart cursor="pointer" color={basketColor} size={20} />
        </div>
      </div>
      <form ref={reg} className="registration">
        <input
          ref={email}
          className="registration-email"
          type="email"
          placeholder="Введите email"
        />
        <input
          ref={password}
          className="registration-password"
          type="password"
          placeholder="Введите пароль"
        />
        <input
          className="registration-submit"
          onClick={(e) => {
            e.preventDefault();
            if (email.current.value && password.current.value) {
              changeProfileColor();
              dispatch({
                type: "CHANGE_EMAIL_PASSWORD",
                email: email.current.value,
                password: password.current.value,
              });
              email.current.value = password.current.value = "";
              changeValue("Вход");
            }
          }}
          type="submit"
          value={value}
        />
      </form>
      <div className="buy">ff</div>
    </div>
  );
}
