import React from "react";
import {BrowserRouter, Routes, Route} from "react-router-dom";
import {createStore} from "redux";
import {Provider} from "react-redux";

import "./components/styles/main.css";
import Good from "./pages/Good";
import Main from "./pages/Main";

function App() {
  const defaultState = {
    profileColor: "white",
    basketColor: "white",
    userEmail: "",
    userPassword: "",
    basket: [],
  };
  const reducer = (state = defaultState, action) => {
    let mass;
    switch (action.type) {
      case "CHANGE_PROFILE_COLOR":
        return {...state, profileColor: action.color};
      case "CHANGE_BASKET_COLOR":
        return {...state, basketColor: action.color};
      case "CHANGE_EMAIL_PASSWORD":
        return {
          ...state,
          userEmail: action.email,
          userPassword: action.password,
        };
      case "ADD_TO_BASKET":
        mass = state.basket;
        if (mass) {
          for (let i = 0; i < mass.length; i++) {
            if (mass[i].name === action.name) {
              mass[i].num += action.num;
              return {...state, basket: [...mass]};
            }
          }
          return {
            ...state,
            basket: [...mass, {name: action.name, num: action.num}],
          };
        }
        return {...state, basket: [{name: action.name, num: action.num}]};
      case "REMOVE_FROM_BASKET":
        mass = state.basket;
        if (mass) {
          for (let i = 0; i < mass.length; i++) {
            if (mass[i].name === action.name) {
              mass[i].num -= action.num;
              if (mass[i].num < 1) {
                mass = mass.filter((el) => !(el.name === action.name));
                return {
                  ...state,
                  basket: [...mass],
                };
              }
              return {...state, basket: [...mass]};
            }
          }
        }
        return state;
      default:
        return state;
    }
  };
  const store = createStore(reducer);
  return (
    <Provider store={store}>
      <BrowserRouter>
        <Routes>
          <Route path="/main" element={<Main />} />
          <Route path="/good/:name" element={<Good />} />
          <Route path="*" element={<Main />} />
        </Routes>
      </BrowserRouter>
    </Provider>
  );
}

export default App;
