import React, {useRef, useState, useEffect} from "react";
import "./styles/header.css";
import {FiShoppingCart} from "react-icons/fi";
import {useNavigate} from "react-router-dom";
import {Link} from "react-router-dom";
import {CgProfile} from "react-icons/cg";
import {useDispatch, useSelector} from "react-redux";
import {GrClose} from "react-icons/gr";
import {AiOutlinePlus, AiOutlineMinus} from "react-icons/ai";
import char from "./base.json";

export default function Header(props) {
  const [value, changeValue] = useState("Регистрация");
  const dispatch = useDispatch();
  const profileColor = useSelector((state) => state.profileColor);
  const basketColor = useSelector((state) => state.basketColor);
  const userEmail = useSelector((state) => state.userEmail);
  const userPassword = useSelector((state) => state.userPassword);
  const basket = useSelector((state) => state.basket);
  const [price, changePrice] = useState("");
  function changeProfileColor() {
    dispatch({type: "CHANGE_PROFILE_COLOR", color: "#3cba0c"});
  }
  const email = useRef();
  const password = useRef();
  const reg = useRef();
  const [allGoods, changeGoods] = useState("");
  function registration() {
    if (userEmail && userPassword) {
      email.current.value = userEmail;
      password.current.value = userPassword;
    }
    reg.current.classList.toggle("registrationWidth");
  }
  let change = 0;
  useEffect(() => {
    changeGoods(basket);
    console.log(basket);
  }, [basket, change]);
  allGoods.map((el) => {
    changePrice(char[el.name].price * el.num + price);
  });
  function buying(el) {
    return (
      <div key={el.name} className="buy-goods-good">
        <img src={char[el.name].src1} />
        <div className="buy-goods-good-info">
          <p className="buy-goods-good-info-name">
            {el.name == "monopoly"
              ? "Монополия"
              : el.name == "zombie"
              ? "Зомби в доме"
              : el.name == "mafia"
              ? "Мафия"
              : "Манчкин"}
          </p>
          <div className="buy-goods-good-info-price">
            <div className="buy-goods-good-info-price-iteration">
              <AiOutlineMinus
                cursor="pointer"
                onClick={() => {
                  dispatch({
                    type: "REMOVE_FROM_BASKET",
                    name: el.name,
                    num: 1,
                  });
                }}
                size={10}
              />
              <div>{el.num}</div>
              <AiOutlinePlus
                cursor="pointer"
                onClick={() => {
                  change++;
                  console.log(change);
                  dispatch({
                    type: "ADD_TO_BASKET",
                    name: el.name,
                    num: 1,
                  });
                }}
                size={10}
              />
            </div>
            <p className="buy-goods-good-info-price-multiply">
              x {char[el.name].price} <span className="byn"> руб.</span>
            </p>
          </div>
        </div>
        <GrClose
          onClick={() => {
            dispatch({
              type: "REMOVE_FROM_BASKET",
              name: el.name,
              num: 10000,
            });
          }}
          className="cross"
          cursor={"pointer"}
        />
      </div>
    );
  }
  return (
    <div className="head">
      <div className="header">
        <img src="/images/logo.png" alt="Figrolls" className="header__logo" />
        <div className="header__main">
          <Link to="/main">Главная</Link>
          <p onClick={() => console.log(basket)}>О нас</p>
          <p>Контакты</p>
        </div>
        <div className="header__cart">
          <CgProfile
            onClick={() => registration()}
            cursor="pointer"
            color={profileColor}
            size={20}
          />
          <FiShoppingCart cursor="pointer" color={basketColor} size={20} />
        </div>
      </div>
      <form ref={reg} className="registration">
        <input
          ref={email}
          className="registration-email"
          type="email"
          placeholder="Введите email"
        />
        <input
          ref={password}
          className="registration-password"
          type="password"
          placeholder="Введите пароль"
        />
        <input
          className="registration-submit"
          onClick={(e) => {
            e.preventDefault();
            if (email.current.value && password.current.value) {
              changeProfileColor();
              dispatch({
                type: "CHANGE_EMAIL_PASSWORD",
                email: email.current.value,
                password: password.current.value,
              });
              email.current.value = password.current.value = "";
              changeValue("Вход");
            }
          }}
          type="submit"
          value={value}
        />
      </form>
      <div className="buy">
        <div className="buy-letters">Ваша корзина({allGoods.length})</div>
        <div className="buy-goods">
          {allGoods
            ? allGoods.map((el) => buying(el))
            : "Вы пока что не положили ничего в корзину"}
        </div>
        <div className="buy-pruschare">
          <div className="buy-pruschare-price">
            <div>Сумма</div>
            <p>
              {price}
              <span className="byn"> руб.</span>
            </p>
          </div>
          <div className="buy-pruschare-submit">Покупка</div>
        </div>
      </div>
      <div className="shadow"></div>
    </div>
  );
}
