import axios from "axios";
import React, {useEffect, useState} from "react";
import "./styles/comments.css";

export default function Comments() {
  useEffect(() => {
    comm();
  }, []);
  const [comments, changeComments] = useState("");
  const [commentsOnPage, changeCommentsOnPage] = useState("");
  const [num, changeNum] = useState(10);
  useEffect(() => {
    if (comments.data) {
      changeCommentsOnPage(comments.data.slice(0, num));
    }
  }, [num]);
  async function comm() {
    const response = await axios.get(
      "https://jsonplaceholder.typicode.com/comments"
    );
    changeComments(response);
    changeCommentsOnPage(response.data.slice(0, 10));
  }
  window.onscroll = function (ev) {
    if (
      window.innerHeight + window.pageYOffset + 1 >=
      document.body.offsetHeight
    ) {
      changeNum(num + 10);
    }
  };
  return (
    <div className="comments">
      {commentsOnPage ? (
        commentsOnPage.map((el) => (
          <div key={el.id} className="comment">
            <div
              style={{
                backgroundColor:
                  "rgb(" +
                  Math.round((Math.random() * 1000) % 255) +
                  "," +
                  Math.round((Math.random() * 1000) % 255) +
                  "," +
                  Math.round((Math.random() * 1000) % 255) +
                  ")",
              }}
              className="comment-avatar"
            ></div>
            <div className="comment-body">
              <div className="comment-info">
                <p className="comment-info-name">{el.name}</p>
                <p
                  id={el.email}
                  onClick={(el) => {
                    alert(el.target.id);
                  }}
                  className="comment-"
                  title={el.email}
                  className="comment-info-email"
                >
                  i
                </p>
              </div>
              <p className="comment-content">{el.body}</p>
            </div>
          </div>
        ))
      ) : (
        <div className="loading-parent">
          <div className="loading"></div>
        </div>
      )}
    </div>
  );
}
