import React from "react";
import {useSelector} from "react-redux";
import "./styles/creation.css";

export default function CreateComment() {
  const email = useSelector((state) => state.userEmail);
  return (
    <div className="creation">
      <h5>Нам важно ваше мнение!</h5>
      {!email ? (
        <p className="creation-request">
          Зарегистрируйтесь, чтобы оставить комментарий
        </p>
      ) : (
        <div>
          <p className="creation-request">
            Расскажите о вашем опыте работы с нами
          </p>
          <div className="creation-name">
            <input type="text" placeholder="Тема комментария" />
            <p>Ваш email: {email}</p>
          </div>
          <textarea width="700px" placeholder="Комментарий" />
        </div>
      )}
    </div>
  );
}
