import React from "react";
import "./styles/char.css";
import char from "./base.json";

export default function Char() {
  let chosen = "desc";
  function changeChoose(id) {
    chosen = id;
  }
  return (
    <div className="char">
      <div className="char__menu">
        <p
          onClick={(el) => changeChoose(el.target.id)}
          id="desc"
          className="char__menu-opt"
        >
          Описание
        </p>
        <p
          onClick={(el) => changeChoose(el.target.id)}
          id="char"
          className="char__menu-opt"
        >
          Характеристики
        </p>
        <p
          onClick={(el) => changeChoose(el.target.id)}
          id="vid"
          className="char__menu-opt"
        >
          Видеообзор
        </p>
      </div>
      {char["monopoly"].chosen}
    </div>
  );
}
