import React, {useRef} from "react";
import "./styles/header.css";
import {FiShoppingCart} from "react-icons/fi";
import {useNavigate} from "react-router-dom";
import {Link} from "react-router-dom";
import {CgProfile} from "react-icons/cg";
import {useDispatch, useSelector} from "react-redux";

export default function Header() {
  const dispatch = useDispatch();
  const profileColor = useSelector((state) => state.profileColor);
  function changeProfileColor() {
    dispatch({type: "CHANGE_COLOR", color: "#3cba0c"});
  }
  const email = useRef();
  return (
    <div>
      <div className="header">
        <img src="/images/logo.png" alt="Figrolls" className="header__logo" />
        <div className="header__main">
          <Link to="/main">Главная</Link>
          <p>О нас</p>
          <p>Контакты</p>
        </div>
        <div className="header__cart">
          <CgProfile
            onClick={() => {
              changeProfileColor();
            }}
            cursor="pointer"
            color={profileColor}
            size={20}
          />
          <FiShoppingCart cursor="pointer" color={"white"} size={20} />
        </div>
      </div>
      <div className="registration">
        <form>
          <input ref={email} className="registration-email" type="email" />
          <input className="registration-password" type="password" />
          <input
            onClick={() => {
              console.log(email.current.value);
            }}
            type="submit"
          />
        </form>
      </div>
    </div>
  );
}
