import React, {useState, useEffect} from "react";
import char from "./base.json";

export default function Images(props) {
  const [img, changeImg] = useState("1");
  function change(id) {
    document.getElementById(img).style.borderColor = "#3f3a60";
    document.getElementById(id).style.borderColor = "#3cba0c";
    changeImg(id);
  }
  return (
    <div className="images">
      <div className="images-choose">
        <img
          onClick={(el) => change(el.target.id)}
          id="1"
          src={char[props.name].src1}
          alt="Картинка не загружена"
        />
        <img
          onClick={(el) => change(el.target.id)}
          id="2"
          src={char[props.name].src2}
          alt="Картинка не загружена"
        />
        <img
          onClick={(el) => change(el.target.id)}
          id="3"
          src={char[props.name].src3}
          alt="Картинка не загружена"
        />
      </div>
      <img
        className="images-look"
        src={char[props.name]["src" + img]}
        alt="Картинка не загружена"
      />
    </div>
  );
}
