import React, {useState} from "react";
import Char from "./Char";
import {useParams} from "react-router-dom";
import "./styles/info.css";
import char from "./base.json";

export default function Info() {
  const params = useParams();
  const [num, changeNum] = useState(1);
  let header;
  switch (params.name) {
    case "monopoly":
      header = "Монополия";
      break;
    case "zombie":
      header = "Зомби в доме";
      break;
    case "mafia":
      header = "Мафия";
      break;
    case "munchkin":
      header = "Манчкин";
      break;
  }
  return (
    <section className="game">
      <div className="game__info">
        <img
          className="game__info-look"
          src={char[params.name].src}
          alt="Картинка не загружена"
        />
        <div className="game__info__data">
          <h2 className="game__info__data-name">{header}</h2>
          <Char name={params.name} />
          <div className="game__info__data-desc">
            <div className="game__info__data-desc-buy">
              <div>
                <p>{char[params.name].price}</p>
              </div>
              <div className="buttons">
                <div className="iteration">
                  <div
                    className="it"
                    onClick={() => {
                      if (num > 0) {
                        changeNum(num - 1);
                      }
                    }}
                  >
                    -
                  </div>
                  <div>{num}</div>
                  <div className="it" onClick={() => changeNum(num + 1)}>
                    +
                  </div>
                </div>
                <button>Приобрести</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
