import React from "react";
import "./styles/info.css";

export default function Info() {
  let mass = [
    {param: "Количество игроков", desc: "2-6"},
    {param: "Возраст игроков", desc: "8+"},
    {param: "Время игры", desc: "60-120"},
  ];
  return (
    <section className="game">
      <h2 className="game-name">Монополия</h2>
      <div className="game_info">
        <img
          className="game_info-look"
          src="images/games/monopoly.png"
          alt="Картинка не загружена"
        />
        <table className="game_info-desc">
          <tbody>
            {mass.map((el) => (
              <tr key={el.param}>
                <td>{el.param}</td>
                <td style={{textAlign: "center"}}>{el.desc}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
}
