import React, {useState} from "react";
import "./styles/char.css";
import char from "./base.json";

export default function Char() {
  const [chosen, choose] = useState("desc");
  function changeChoose(id) {
    choose(id);
  }

  return (
    <div className="char">
      <div className="char__menu">
        <p
          onClick={(el) => changeChoose(el.target.id)}
          id="desc"
          className="char__menu-opt"
        >
          Описание
        </p>
        <p
          onClick={(el) => changeChoose(el.target.id)}
          id="char"
          className="char__menu-opt"
        >
          Характеристики
        </p>
        <p
          onClick={(el) => changeChoose(el.target.id)}
          id="vid"
          className="char__menu-opt"
        >
          Видеообзор
        </p>
      </div>
      {chosen == "desc" ? (
        char["monopoly"][chosen]
      ) : chosen == "char" ? (
        <table>
          {char["monopoly"][chosen].map((el) => (
            <tr>
              <td>{el.key}</td>
              <td>{el.desc}</td>
            </tr>
          ))}
        </table>
      ) : (
        2
      )}
    </div>
  );
}
