import React, {useState} from "react";
import Char from "./Char";
import {useParams} from "react-router-dom";
import "./styles/info.css";
import char from "./base.json";
import {useDispatch, useSelector} from "react-redux";
import Images from "./Images";

export default function Info(props) {
  const dispatch = useDispatch();
  const basket = useSelector((state) => state.basket);
  const params = useParams();
  const [num, changeNum] = useState(1);
  let header;
  switch (params.name) {
    case "monopoly":
      header = "Монополия";
      break;
    case "zombie":
      header = "Зомби в доме";
      break;
    case "mafia":
      header = "Мафия";
      break;
    case "munchkin":
      header = "Манчкин";
      break;
  }
  return (
    <section className="game">
      <div className="game__info">
        <Images name={params.name} />
        <div className="game__info__data">
          <h2 className="game__info__data-name">{header}</h2>
          <Char name={params.name} />
          <div className="game__info__data-desc">
            <div className="game__info__data-desc-buy">
              <p className="show-price">
                {char[params.name].price * num}{" "}
                <span className="byn"> руб.</span>
              </p>
              <div className="iteration">
                <div
                  className="it"
                  onClick={() => {
                    if (num > 1) {
                      changeNum(num - 1);
                    }
                  }}
                >
                  -
                </div>
                <div className="num">{num}</div>
                <div className="it" onClick={() => changeNum(num + 1)}>
                  +
                </div>
              </div>
              <button
                onClick={() => {
                  dispatch({type: "CHANGE_BASKET_COLOR", color: "#3cba0c"});
                  dispatch({
                    type: "ADD_TO_BASKET",
                    name: params.name,
                    num: num,
                  });
                  props.changeGoods(basket);
                }}
              >
                Приобрести
              </button>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}
